class DataSetLessonsService(
    override val app: ClassChartsClient,
    val ccService: DataSetClassChartsService
) : dev.nikdekur.classcharts.ClassChartsService(), LessonsService {

    override suspend fun getLessons(date: LocalDate): LessonsService.LessonsData {
        val timeZone = TimeZone.currentSystemDefault()
        val lesson = ccService.getForDate(date).first
        return object : LessonsService.LessonsData {
            override val startTime = date.atTime(0, 0).toInstant(timeZone)
            override val endTime = date.atTime(23, 59).toInstant(timeZone)
            override val lessons: Collection<Lesson> = lesson
            override val timetableDates = run {
                // Week days (Mon, Tue, Wed, Thu, Fri)
                val offset = date.dayOfWeek.isoDayNumber
                (1..7).map { date.plus(it - offset, DateTimeUnit.DAY) }
            }
            override val periods = run {
                lesson.map { lesson ->
                    val startTime = lesson.startTime.toLocalDateTime(timeZone).time
                    val endTime = lesson.endTime.toLocalDateTime(timeZone).time
                    object : LessonsService.Period {
                        override val number = lesson.periodNumber.toInt()
                        override val startTime = startTime
                        override val endTime = endTime
                    }
                }.distinct()
            }
        }
    }
}


